(14, 14, 'fr', 'La 8ème édition de la journée scientifique annuelle 2018 - Laboratoire de Physique de la Matière et de Rayonnements  (LPMR)', '<p><span style=\"font-size:14px\">Laboratoire de Physique de la Mati&egrave;re et de Rayonnements&nbsp;(LPMR)&nbsp;Organise :</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#e74c3c\"><strong><span style=\"font-size:16px\">La 8&egrave;me &eacute;dition de la journ&eacute;e scientifique<br />\r\nannuelle 2018</span></strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"font-size:16px\"><span style=\"background-color:#dddddd\">Samedi 03 Novembre 2018</span></span></strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"font-size:16px\"><span style=\"background-color:#dddddd\">B&acirc;timent de recherche</span></span></strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"font-size:16px\"><span style=\"background-color:#dddddd\">Salle de r&eacute;union (8H30&rsquo;)</span></span></strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-size:16px\"><span style=\"background-color:#ffffff\">Contact :</span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:16px\"><span style=\"background-color:#ffffff\">F. FETHI, Directeur du laboratoire</span><br />\r\n<span style=\"background-color:#ffffff\">PMR</span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:16px\"><span style=\"background-color:#ffffff\">E-mail: fethi.fouad@yahoo.fr</span></span></strong></p>'),
(15, 15, 'fr', 'La Culture Arabe Au Maroc : Histoire, Défis et Perspectives', '<p><span style=\"font-size:14px\">Dans le cadre de ses activit&eacute;s, le Centre des Etudes Doctorales en Sciences et Techniques organise une&nbsp;s&eacute;rie de conf&eacute;rences sur la culture Arabo-Musulmane au Maroc. Cette s&eacute;rie de Conf&eacute;rences va constituer une&nbsp;formation g&eacute;n&eacute;rale qui entre dans le cadre des Formations organis&eacute;es par le CEDoc-ST.<br />\r\nLes conf&eacute;rences seront organis&eacute;es durant le premier semestre de l&rsquo;ann&eacute;e universitaire 2018/2019 comme suit :</span></p>\r\n\r\n<h4 style=\"text-align:center\"><a href=\"https://drive.google.com/open?id=0B3uws_5Pu60Hd1R3ajJkTG8zUWI3WVltb3hTWEFpZ3p1X21J\" target=\"_blank\"><strong>Affiche</strong></a></h4>'),
