(151, 133, 'fr', 'Avis aux Étudiants', '<h1 style=\"text-align:center\"><span style=\"color:#2c3e50\"><span style=\"font-size:20px\"><u><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Avis aux &Eacute;tudiants</strong></span></u></span></span></h1>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Les &eacute;tudiants <strong><span style=\"color:#e74c3c\"><u>qui ne figurent pas sur les listes des examens</u></span></strong> ne sont pas autoris&eacute;s &agrave; acc&eacute;der &agrave; la salle d&rsquo;examen, et doivent r&eacute;gulariser leur situation aupr&egrave;s de la scolarit&eacute;.</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Les copies d&rsquo;examens doivent comporter, en plus du nom et pr&eacute;nom, </span></span><span style=\"color:#e74c3c\"><strong><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><u>les num&eacute;ros APOGEE et d&rsquo;examen</u></span></span></strong></span><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"> pour &eacute;viter toute confusion entre les &eacute;tudiants (de m&ecirc;me nom et pr&eacute;nom). </span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><a href=\"https://drive.google.com/file/d/1T03_LssWj96qBh5T-SHQr_wtdjxumLCf/view?usp=sharing\" target=\"_blank\"><span style=\"font-size:20px\"><u><strong>Avis</strong></u></span></a></p>', NULL),
