(203, 185, 'fr', 'Avis de l\'ouverture de candidature à l\'habilitation universitaire', '<h4><img alt=\"\" src=\"http://fso.ump.ma/uploads/images/1/Documents/5dbcc21f80369.png\" style=\"float:right; height:840px; width:594px\" /></h4>\r\n\r\n<ul>\r\n	<li>\r\n	<h4><span style=\"font-size:18px\"><strong><a href=\"https://drive.google.com/file/d/0B2XN0ygNR0QXNnJmeXhmSUd1TUFHRlFoTXFaTnA1NG5LdWgw/view?usp=sharing\" target=\"_blank\">Formulaire de candidature &agrave; l&rsquo;habilitation universitaire - Session d&rsquo;automne 2019</a></strong></span></h4>\r\n	</li>\r\n	<li>\r\n	<h4><span style=\"font-size:18px\"><strong><a href=\"https://drive.google.com/file/d/0B2XN0ygNR0QXUUk0Mm5MOHNKRm1wMmNfZFRKZ3dMb2JnMU0w/view?usp=sharing\" target=\"_blank\">Modalit&eacute;s relatives &agrave; l&rsquo;organisation de l&rsquo;habilitation universitaire</a></strong></span></h4>\r\n	</li>\r\n	<li>\r\n	<h4><span style=\"font-size:18px\"><strong><a href=\"https://drive.google.com/file/d/0B2XN0ygNR0QXZDRES2p3VVE3emt6dVhFMFYzengxODA2ZFlV/view?usp=sharing\" target=\"_blank\">Page de garde</a></strong></span></h4>\r\n	</li>\r\n	<li>\r\n	<h4><span style=\"font-size:18px\"><strong><a href=\"https://drive.google.com/file/d/0B2XN0ygNR0QXNXhlU0VTVlQxUGlqcEV1RFhvcDd6eVJ6ZnVV/view?usp=sharing\" target=\"_blank\">Avis sur l&rsquo;ouverture de candidature &agrave; l&rsquo;Habilitation Universitaire&nbsp;: session automne 2019</a></strong></span></h4>\r\n	</li>\r\n</ul>', NULL),
